// src/components/TodoList.js

import { useState } from "react";

function TodoList() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState("");

  function addTask() {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask("");
    }
  }

  function removeTask(index) {
    setTasks(tasks.filter((_, i) => i !== index));
  }

  return (
    <div className="todo-list-card"> {/* Changed class name for specific styling */}
      <h3>Yapƒ±lacaklar üìù</h3>
      <div className="input-group">
        <input
          value={task}
          onChange={e => setTask(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && addTask()}
          placeholder="Yeni g√∂rev..."
        />
        <button onClick={addTask}>Ekle</button>
      </div>
      <ul className="tasks-list">
        {tasks.map((t, i) => (
          <li key={i}>
            <span className="task-text">{t}</span> 
            <button className="delete-btn" onClick={() => removeTask(i)}><i className="bi bi-trash"></i></button>
          </li>
        ))}
        {tasks.length === 0 && <li className="empty-message">Hen√ºz bir g√∂rev yok!</li>}
      </ul>
    </div>
  );
}

export default TodoList;
