// src/components/TodoList.js

import { useState, useEffect } from "react";

// localStorage'da kullanƒ±lacak anahtar
const STORAGE_KEY = 'animedoro-tasks';

function TodoList() {
  // 1. G√∂revleri localStorage'dan y√ºkle
  const [tasks, setTasks] = useState(() => {
    try {
      const savedTasks = localStorage.getItem(STORAGE_KEY);
      // Kayƒ±tlƒ± g√∂rev varsa, JSON'dan parse et, yoksa bo≈ü dizi d√∂nd√ºr
      return savedTasks ? JSON.parse(savedTasks) : [];
    } catch (e) {
      console.error("Local Storage'dan g√∂rev y√ºklenirken hata olu≈ütu:", e);
      return [];
    }
  });

  const [task, setTask] = useState("");

  // 2. tasks state'i her deƒüi≈ütiƒüinde localStorage'a kaydet
  useEffect(() => {
    try {
      // tasks dizisini JSON string'ine √ßevirip kaydet
      localStorage.setItem(STORAGE_KEY, JSON.stringify(tasks));
    } catch (e) {
      console.error("Local Storage'a g√∂rev kaydedilirken hata olu≈ütu:", e);
    }
  }, [tasks]); // tasks baƒüƒ±mlƒ±lƒ±ƒüƒ± sayesinde her deƒüi≈üimde √ßalƒ±≈üƒ±r

  function addTask() {
    // Ekleme i≈ülevi aynƒ± kalƒ±r, ancak ≈üimdi state deƒüi≈ütiƒüi i√ßin useEffect otomatik kaydeder.
    if (task.trim()) {
      // G√∂revi listeye eklerken, benzersiz bir ID kullanmak yerine ≈üimdilik sadece metni ekliyoruz.
      // Not: removeTask zaten indekse g√∂re √ßalƒ±≈ütƒ±ƒüƒ± i√ßin burada sadece metin yeterlidir.
      setTasks([...tasks, task.trim()]);
      setTask("");
    }
  }

  function removeTask(indexToRemove) {
    // Silme i≈ülevi: Belirtilen indeksteki elemanƒ± filtrele ve yeni listeyi ayarla.
    // State deƒüi≈ütiƒüi i√ßin useEffect otomatik olarak localStorage'dan da silinmesini saƒülar.
    const newTasks = tasks.filter((_, i) => i !== indexToRemove);
    setTasks(newTasks);
  }

  return (
    <div className="todo-list-card">
      <h3>Yapƒ±lacaklar üìù</h3>
      <div className="input-group">
        <input
          value={task}
          onChange={e => setTask(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && addTask()}
          placeholder="Yeni g√∂rev..."
        />
        <button onClick={addTask}>Ekle</button>
      </div>
      <ul className="tasks-list">
        {tasks.map((t, i) => (
          <li key={i}>
            <span className="task-text">{t}</span> 
            <button className="delete-btn" onClick={() => removeTask(i)}><i className="bi bi-trash"></i></button>
          </li>
        ))}
        {tasks.length === 0 && <li className="empty-message">Hen√ºz bir g√∂rev yok!</li>}
      </ul>
    </div>
  );
}

export default TodoList;
