    setDate(newDate);
    console.log("Se√ßilen Tarih:", newDate.toLocaleDateString('tr-TR'));
  };

  return (
    <div className="calendar-card">
      <h3>Calendar üóìÔ∏è</h3>
      <CalendarComponent 
        onChange={onChange}
        value={date}
        locale="tr-TR"
        className="react-calendar-override"
      />
    </div>
  );
}

export default Calendar;
