// src/components/Calendar.js

import { useState } from 'react';
import CalendarComponent from 'react-calendar';
// Stil dosyasƒ±nƒ± projenize (√∂rneƒüin src/sass/style.css veya App.js) dahil etmeyi unutmayƒ±n.
// Eƒüer Sass kullanƒ±yorsanƒ±z, stil dosyasƒ±nƒ± src/index.js veya App.js i√ßine ekleyin:
// import 'react-calendar/dist/Calendar.css';

function Calendar() {
  // Takvimde se√ßili olan tarihi tutmak i√ßin state
  const [date, setDate] = useState(new Date());

  // Yeni bir tarih se√ßildiƒüinde √ßalƒ±≈üacak fonksiyon
  const onChange = (newDate) => {
    setDate(newDate);
    console.log("Se√ßilen Tarih:", newDate.toLocaleDateString('tr-TR'));
    // ƒ∞stenirse buraya se√ßilen tarihe g√∂re yapƒ±lacaklar listesi gibi ekstra bir mantƒ±k eklenebilir.
  };

  return (
    <div className="calendar-card">
      <h3>Takvim üóìÔ∏è</h3>
      <CalendarComponent 
        onChange={onChange}
        value={date}
        locale="tr-TR" // Takvimi T√ºrk√ße yapmak i√ßin
        className="react-calendar-override" // √ñzelle≈ütirilmi≈ü stiller i√ßin class ekleyelim
      />
      <p style={{marginTop: '10px', fontSize: '14px'}}>
        Se√ßili Tarih: **{date.toLocaleDateString('tr-TR')}**
      </p>
    </div>
  );
}

export default Calendar;
