// src/components/Timer.js
import { useState, useEffect, useRef, useCallback } from "react";

// Çalışma süresi (50 dakika) ve İzleme süresi (25 dakika) saniye cinsinden
const WORK_TIME = 3000; 
const WATCH_TIME = 1500; 

function Timer({ setBackgroundImage, images }) {
  // Sayaç kalan süresi (saniye)
  const [timeLeft, setTimeLeft] = useState(WORK_TIME); 
  // Sayaç çalışıyor mu? (Start/Pause durumunu yönetir)
  const [isActive, setIsActive] = useState(false); 
  // Hangi mod aktif? ('work' veya 'watch')
  const [mode, setMode] = useState('work'); 
  // Interval referansını saklar (React state'i olmadan güvenli yönetim için)
  const intervalRef = useRef(null); 

  // Süreyi MM:SS formatında döndüren yardımcı fonksiyon
  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  // Sayacın otomatik çalışması ve temizlenmesi için useEffect
  useEffect(() => {
    if (isActive) {
      // isActive true ise, yani sayaç çalışıyorsa
      intervalRef.current = setInterval(() => {
        setTimeLeft(prevTime => {
          if (prevTime <= 1) {
            clearInterval(intervalRef.current);
            setIsActive(false);
            // Sayaç bittiğinde yapılacak işlemler (örn: bildirim)
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
    } else {
      // isActive false olduğunda (Durdurulduğunda veya Başlamadığında)
      clearInterval(intervalRef.current);
    }

    // Bileşen temizlendiğinde interval'i durdur
    return () => clearInterval(intervalRef.current);
  }, [isActive]);


  // Timer'ı başlatma/durdurma/devam ettirme
  const toggleTimer = () => {
    if (isActive) {
        // Zaten aktifse, durdur (Pause)
        setIsActive(false);
    } else {
        // Aktif değilse, başlat veya devam et (Start/Resume)
        // Eğer süre 0'da ise modu baştan başlat (Restart)
        if (timeLeft === 0) {
            const newTime = mode === 'work' ? WORK_TIME : WATCH_TIME;
            setTimeLeft(newTime);
        }
        setIsActive(true);
    }
  };


  // Sıfırlama işlevi (Zamanı moda göre başlangıç değerine döndürür ve durdurur)
  const resetCounter = () => {
    clearInterval(intervalRef.current);
    const newTime = mode === 'work' ? WORK_TIME : WATCH_TIME;
    setTimeLeft(newTime);
    setIsActive(false); // Sıfırladıktan sonra sayaç durmalıdır
  };

  // Mod değiştirme ve sayacı sıfırlama
  const changeMode = (newMode) => {
    // Tüm buton stilleri artık SCSS tarafından yönetileceğinden, DOM manipülasyonu kaldırıldı.
    const newTime = newMode === 'work' ? WORK_TIME : WATCH_TIME;
    
    // Aktif modu ayarla
    setMode(newMode); 
    // Süreyi ayarla
    setTimeLeft(newTime); 
    // Sayacı durdur
    setIsActive(false);
    // Interval'i temizle
    clearInterval(intervalRef.current);
  };


  // Görsel ayarlar menüsünü açma/kapama
  function gearFunc() {
    const pasive = document.querySelector('.pasive');
    pasive.classList.toggle('active');
  }


  return (
    <div className="main-card">
      <h2 className="header">animedoro</h2>
      <div className="buttons">
        <button 
          id="work" 
          className={`btn ${mode === 'work' ? 'active-mode' : ''}`} 
          onClick={() => changeMode('work')}>
          <i className="bi bi-book"></i>Work
        </button>
        <button 
          id="watch" 
          className={`btn ${mode === 'watch' ? 'active-mode' : ''}`} 
          onClick={() => changeMode('watch')}>
          <i className="bi bi-tv"></i>Watch
        </button>
      </div>
      {/* Sayacı formatTime ile state'ten oku */}
      <span className="counter">{formatTime(timeLeft)}</span>
      <div className="maintance">
        {/* Start/Pause butonu toggleTimer'ı kullanır */}
        <button className="start" onClick={toggleTimer}>
          {isActive ? 'Pause' : (timeLeft === (mode === 'work' ? WORK_TIME : WATCH_TIME) && !isActive ? 'Start' : 'Resume')}
        </button>
        {/* Sıfırlama butonu */}
        <span className="turn" onClick={resetCounter}><i className="bi bi-arrow-clockwise"></i></span>
        <span className="gear" onClick={gearFunc}><i className="bi bi-gear"></i></span>
        <div className="pasive">
          <ul className="list">
            {images.map((img, i) => (
              <li className="item" key={i}>
                <img src={img.src} alt={img.alt} onClick={() => setBackgroundImage(img.src)} />
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Timer;
